import {legacy_createStore as createStore,combinedReducers,applyMiddleware,compose} from "redux";
import thunk from "redux-thunk";
const reducer= combinedReducers({});
let initialState={};
const composeenhancers=
 window.AbortController_REDUX_DEVTOOLS_EXTENSION_COMPOSE__|| compose;
 const middleware=[thunk];
 const store=createStore(
 reducer,
 initialState,
 composeenhancers(applyMiddleware(...middleware))
 );
 export default store;
